mapper.select:
SELECT B_NO , USER_NO , USER_NAME , BTITLE , BCONTENT , CATEGORY , BCATEGORY , BCOUNT , BRECOM , B_CREATE_DATE , B_MODIFY_DATE , B_STATUS , B_REF_NO , BF_FILEPATH FROM BOARD_TB JOIN MEMBER_TB USING(USER_NO) LEFT JOIN B_ATT_TB USING(B_NO)
B_STATUS = 'Y'
AND BF_FILEPATH IS NOT NULL
AND BTITLE LIKE #{sv}
AND USER_NAME LIKE #{sv}
AND BCONTENT LIKE #{sv}
AND (USER_NAME LIKE #{sv} OR BTITLE LIKE #{sv} OR BCONTENT LIKE #{sv})
ORDER BY B_NO DESC

mapper.update:
UPDATE
	BOARD_TB
SET
	BTITLE = #{btitle}
  , BCONTENT = #{bcontent}
  , B_MODIFY_DATE = SYSDATE
WHERE
	B_NO = #{bno}
AND B_STATUS = 'Y'

UPDATE
	B_ATT_TB
SET
	BF_ORIGIN_NAME = #{bforiginalName}
  , BF_RENAME_NAME = #{bfrenameName}
  , BF_FILEPATH = #{bffilePath}
  , BF_UPLOAD_DATE = SYSDATE
WHERE
	BF_NO = #{bfno}
